import React, { useState, useEffect } from "react";

function Form({ setForm }) {
  const [contacts, setContacts] = useState({
    fullName: "",
    phone: "",
  });

  const onChange = (e) => {
    setContacts({
      ...contacts,
      [e.target.name]: e.target.value,
    });
  };

  const onSubmit = (e) => {
    e.preventDefault();

    if (contacts.fullName === "" || contacts.phone === "") {
      return false;
    }

    setForm([contacts]);
    console.log(contacts);
  };

  return (
    <form onSubmit={onSubmit}>
      <div>
        <input
          name="fullname"
          placeholder="Fullname"
          onChange={onChange}
        ></input>
      </div>
      <div>
        <input
          name="phone_number"
          placeholder="Phone Number"
          onChange={onChange}
        ></input>
      </div>
      <div>
        <button>Add</button>
      </div>
    </form>
  );
}

export default Form;
